import React, { useState } from 'react';
import './StudentPage.css';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [newStudentName, setNewStudentName] = useState('');

  const handleAddStudent = () => {
    if (newStudentName) {
      setStudents([...students, newStudentName]);
      setNewStudentName('');
    }
  };

  const handleDeleteStudent = (index) => {
    const updatedStudents = students.filter((_, i) => i !== index);
    setStudents(updatedStudents);
  };

  const handleUpdateStudent = (index, newName) => {
    const updatedStudents = [...students];
    updatedStudents[index] = newName;
    setStudents(updatedStudents);
  };

  return (
    <div className="student-page">
      <h2>Student Page</h2>

      <div className="student-form">
        <input
          type="text"
          value={newStudentName}
          onChange={(e) => setNewStudentName(e.target.value)}
          placeholder="Enter student name"
        />
        <button onClick={handleAddStudent}>Add Student</button>
      </div>

      <ul className="student-list">
        {students.map((student, index) => (
          <li key={index} className="student-item">
            <span>{student}</span>
            <div className="student-actions">
              <button onClick={() => handleUpdateStudent(index, `${student} Updated`)}>
                Update
              </button>
              <button onClick={() => handleDeleteStudent(index)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentPage;
