import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Student.css';
import Navbar from '../navbar/Navbar';
import { BASE_URL } from '../../config';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    id: '',
    email: '',
    phone: '',
    selectedBooks: [],
  });
  const [editingIndex, setEditingIndex] = useState(-1);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/students`);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent((prevStudent) => ({ ...prevStudent, [name]: value }));
  };

  const isDuplicateId = (id) => {
    return students.some((student) => student.id === id);
  };

  const handleAddStudent = async () => {
    if (newStudent.name && newStudent.id && newStudent.email && newStudent.phone) {
      if (isDuplicateId(newStudent.id)) {
        alert('Student with the same ID already exists.');
      } else {
        try {
          const response = await axios.post(`${BASE_URL}/students`, newStudent);
          const addedStudent = response.data; // Assuming the response contains the added student
          setStudents([...students, addedStudent]);
          setNewStudent({
            name: '',
            id: '',
            email: '',
            phone: '',
            selectedBooks: [],
          });
        } catch (error) {
          console.error('Error adding student:', error);
        }
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  const handleDeleteStudent = async (index) => {
    try {
      const deletedStudent = students[index];
      await axios.delete(`${BASE_URL}/students/${deletedStudent.id}`);
      const updatedStudents = students.filter((student) => student.id !== deletedStudent.id);
      setStudents(updatedStudents);
    } catch (error) {
      console.error('Error deleting student:', error);
    }
  };

  const handleEditStudent = (index) => {
    setEditingIndex(index);
    const selectedStudent = students[index];
    setNewStudent({
      name: selectedStudent.name,
      id: selectedStudent.id,
      email: selectedStudent.email,
      phone: selectedStudent.phone,
      selectedBooks: selectedStudent.selectedBooks,
    });
  };

  const handleUpdateStudent = async () => {
    if (newStudent.name && newStudent.id && newStudent.email && newStudent.phone) {
      if (isDuplicateId(newStudent.id)) {
        alert('Student with the same ID already exists.');
      } else {
        try {
          const updatedStudent = students[editingIndex];
          await axios.put(`${BASE_URL}/students/${updatedStudent.id}`, newStudent);
          const updatedStudents = [...students];
          updatedStudents[editingIndex] = newStudent;
          setStudents(updatedStudents);
          setNewStudent({
            name: '',
            id: '',
            email: '',
            phone: '',
            selectedBooks: [],
          });
          setEditingIndex(-1);
        } catch (error) {
          console.error('Error updating student:', error);
        }
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div>
      <Navbar />
      <div className="student-container">
        <h1>Students</h1>
        <div className="add-student">
          <h2>Add Student</h2>
          <input
            type="text"
            name="name"
            value={newStudent.name}
            onChange={handleInputChange}
            placeholder="Name"
          />
          <input
            type="text"
            name="id"
            value={newStudent.id}
            onChange={handleInputChange}
            placeholder="ID"
          />
          <input
            type="email"
            name="email"
            value={newStudent.email}
            onChange={handleInputChange}
            placeholder="Email"
          />
          <input
            type="tel"
            name="phone"
            value={newStudent.phone}
            onChange={handleInputChange}
            placeholder="Phone"
          />
          <button onClick={handleAddStudent}>Add Student</button>
        </div>
        <div className="student-list">
          <h2>Student List</h2>
          <ul>
            {students.map((student, index) => (
              <li key={student.id}>
                {student.name} - {student.id}
                <button onClick={() => handleDeleteStudent(index)}>Delete</button>
                <button onClick={() => handleEditStudent(index)}>Edit</button>
              </li>
            ))}
          </ul>
        </div>
        {editingIndex !== -1 && (
          <div className="edit-student">
            <h2>Edit Student</h2>
            <input
              type="text"
              name="name"
              value={newStudent.name}
              onChange={handleInputChange}
              placeholder="Name"
            />
            <input
              type="text"
              name="id"
              value={newStudent.id}
              onChange={handleInputChange}
              placeholder="ID"
            />
            <input
              type="email"
              name="email"
              value={newStudent.email}
              onChange={handleInputChange}
              placeholder="Email"
            />
            <input
              type="tel"
              name="phone"
              value={newStudent.phone}
              onChange={handleInputChange}
              placeholder="Phone"
            />
            <button onClick={handleUpdateStudent}>Update Student</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Student;
