import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Student.css';
import Navbar from '../navbar/Navbar';
import { BASE_URL } from '../../config';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [description, setDescription] = useState('');
  const [code, setCode] = useState('');

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/students`);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const addStudent = async () => {
    if (title && author && description && code && selectedGenre) {
      const newStudent = {
        title,
        author,
        description,
        code,
        genre: selectedGenre,
      };

      try {
        const response = await axios.post(`${BASE_URL}/students`, newStudent);
        const addedStudent = response.data; // Assuming the response contains the added student

        setStudents([...students, addedStudent]);
        setTitle('');
        setAuthor('');
        setDescription('');
        setCode('');
        setSelectedGenre('');

        // Fetch the updated students data again
        fetchStudents();
      } catch (error) {
        console.error('Error adding student:', error);
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  const deleteStudent = async (studentId) => {
    try {
      const deletedStudent = students[studentId];

      await axios.delete(`${BASE_URL}/students/${deletedStudent._id}`);
      const updatedStudents = students.filter((student) => student._id !== deletedStudent._id);

      setStudents(updatedStudents);
    } catch (error) {
      console.error('Error deleting student:', error);
    }
  };

  const editStudent = async (studentId) => {
    const bookToEdit = students.find((student) => student._id === studentId);
    if (bookToEdit) {
      setTitle(bookToEdit.title);
      setAuthor(bookToEdit.author);
      setDescription(bookToEdit.description);
      setCode(bookToEdit.code);
      setSelectedGenre(bookToEdit.genre);
    } else {
      alert('Student not found.');
    }
  };

  const genres = [
    'All Students',
    'Undergraduate',
    'Graduate',
    'Postgraduate',
  ];

  return (
    <div>
      <Navbar />
      <h1 className="page-title">Student Management</h1>

      <div className="add-book-form">
        <h2>Add Student</h2>
        <input
          type="text"
          placeholder="Student Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Student Author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
              <table className="students-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Roll No</th>
            <th>Email</th>
            <th>Mobile No</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
                        <tr key={student._id}>
                        <td>{student.title}</td>
                        <td>{student.author}</td>
                        <td>{student.description}</td>
                        <td>{student.code}</td>
                        <td>
                          <button onClick={() => editStudent(student._id)}>Edit</button>
                          <button onClick={() => deleteStudent(student._id)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          );
          };
          
export default Student;
