import React, { useState } from 'react';
import './Login.css'; // Import the CSS file
import { BASE_URL } from '../../config';
import axios from 'axios';

function Login(props) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const login = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(`${BASE_URL}/users/login`, {
        username,
        password,
      });
      const token = response.data.token;
      // Store the token in localStorage or cookies for future use

      console.log('Login successful');
      // Perform any additional actions after successful login
    } catch (error) {
      console.error('Login failed:', error);
      // Handle login failure
    }
  };

  return (
    <div className="login-container">
      <form className="login">
        <h1>Login</h1>
        <input
          type="text"
          placeholder="username"
          value={username}
          onChange={handleUsernameChange}
        />
        <input
          type="password"
          placeholder="password"
          value={password}
          onChange={handlePasswordChange}
        />
        <button onClick={login}>Login</button>
        <button onClick={props.onRegister}>Register</button>
      </form>
    </div>
  );
}

function Registration(props) {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const register = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(`${BASE_URL}/users/register`, {
        username,
        email,
        password,
      });
      console.log('Registration successful');
      // Perform any additional actions after successful registration
    } catch (error) {
      console.error('Registration failed:', error);
      // Handle registration failure
    }
  };

  return (
    <div className="registration-container">
      <form className="registration">
        <h1>Register</h1>
        <input
          type="text"
          placeholder="username"
          value={username}
          onChange={handleUsernameChange}
        />
        <input
          type="email"
          placeholder="email"
          value={email}
          onChange={handleEmailChange}
        />
        <input
          type="password"
          placeholder="password"
          value={password}
          onChange={handlePasswordChange}
        />
        <button onClick={register}>Register</button>
        <button onClick={props.onLogin}>Back to Login</button>
      </form>
    </div>
  );
}

export default function LoginPage(props) {
  const [showRegistration, setShowRegistration] = useState(false);

  const handleRegister = () => {
    setShowRegistration(true);
  };

  const handleLogin = () => {
    setShowRegistration(false);
  };

  return (
    <div>
      {showRegistration ? (
        <Registration onLogin={handleLogin} />
      ) : (
        <Login onRegister={handleRegister} />
      )}
    </div>
  );
}
