import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Books.css';
import Navbar from '../navbar/Navbar';
import { BASE_URL } from '../../config';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [description, setDescription] = useState('');
  const [code, setCode] = useState('');

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/books`);
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const addBook = async () => {
    if (title && author && description && code && selectedGenre) {
      const newBook = {
        title,
        author,
        description,
        code,
        genre: selectedGenre,
      };

      try {
        await axios.post(`${BASE_URL}/books`, newBook);
        fetchBooks();
        setTitle('');
        setAuthor('');
        setDescription('');
        setCode('');
        setSelectedGenre('');
      } catch (error) {
        console.error('Error adding book:', error);
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  const deleteBook = async (bookId) => {
    try {
      await axios.delete(`${BASE_URL}/books/${bookId}`);
      fetchBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  const editBook = async (bookId) => {
    const bookToEdit = books.find((book) => book._id === bookId);
    if (bookToEdit) {
      setTitle(bookToEdit.title);
      setAuthor(bookToEdit.author);
      setDescription(bookToEdit.description);
      setCode(bookToEdit.code);
      setSelectedGenre(bookToEdit.genre);
      deleteBook(bookId);
    }
  };

  const genres = [
    'All Books',
    'Adventure',
    'Mystery',
    'Fantasy',
    'Romance',
    'Sci-Fi',
    'History',
    'Poetry',
    'Children',
  ];

  return (
    <div className="books-page">
      <Navbar />
      <h1 className="page-title">Books Page</h1>

      <div className="add-book-form">
        <h2>Add Book</h2>
        {/* Add your input fields for title, author, description, code, and genre */}
        {/* Add your button for adding a book */}
      </div>

      <div className="table">
        <div className="table-row table-header">
          <div className="table-cell">Book Title</div>
          <div className="table-cell">Book Author</div>
          <div className="table-cell">Book Description</div>
          <div className="table-cell">Book Code</div>
          <div className="table-cell">Genre</div>
          <div className="table-cell">Actions</div>
        </div>
        <div className="table-body">
          {books.length > 0 ? (
            books.map((book) => {
              return (
                <div className="table-row" key={book._id}>
                  <div className="table-cell">{book.title}</div>
                  <div className="table-cell">{book.author}</div>
                  <div className="table-cell">{book.description}</div>
                  <div className="table-cell">{book.code}</div>
                  <div className="table-cell">{book.genre}</div>
                  <div className="table-cell">
                    <button onClick={() => deleteBook(book._id)}>Delete</button>
                    <button onClick={() => editBook(book._id)}>Edit</button>
                  </div>
                </div>
              );
            })
          ) : (
            <p>No books found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Books;
