import React, { useState } from 'react';
import './Home.css';

const Home = () => {
  const [isStudentFlipped, setIsStudentFlipped] = useState(false);
  const [isBooksFlipped, setIsBooksFlipped] = useState(false);

  const flipStudentCard = () => {
    setIsStudentFlipped(!isStudentFlipped);
  };

  const flipBooksCard = () => {
    setIsBooksFlipped(!isBooksFlipped);
  };

  return (
    <div className="container">
      <h1 className="heading">Library Management System</h1>
      <p className="description">
        A comprehensive system to efficiently manage library operations and resources.
      </p>
      <div className={`section ${isStudentFlipped ? 'flipped' : ''}`} onClick={flipStudentCard}>
        <h2>Students</h2>
        {isStudentFlipped && (
          <div className="flip-card-content">
            {/* Contents for the student page */}
            <p>Student Page Content</p>
          </div>
        )}
      </div>
      <div className={`section ${isBooksFlipped ? 'flipped' : ''}`} onClick={flipBooksCard}>
        <h2>Books</h2>
        {isBooksFlipped && (
          <div className="flip-card-content">
            {/* Contents for the books page */}
            <p>Books Page Content</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
