import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Student.css';
import Navbar from '../navbar/Navbar';
import { BASE_URL } from '../../config';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    rollNo: '',
    email: '',
    mobileNo: '',
  });
  const [editingIndex, setEditingIndex] = useState(-1);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/students`);
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent((prevStudent) => ({ ...prevStudent, [name]: value }));
  };

  const isDuplicateRollNo = (rollNo) => {
    return students.some((student) => student.rollNo === rollNo);
  };

  const handleAddStudent = async () => {
    if (newStudent.name && newStudent.rollNo && newStudent.email && newStudent.mobileNo) {
      if (isDuplicateRollNo(newStudent.rollNo)) {
        alert('Student with the same Roll No already exists.');
      } else {
        try {
          const addedStudent = {
            name: newStudent.name,
            rollNo: newStudent.rollNo,
            email: newStudent.email,
            mobileNo: newStudent.mobileNo,
          };

          const response = await axios.post(`${BASE_URL}/students`, addedStudent);
          const { data } = response; // Assuming the response contains the added student

          setStudents((prevStudents) => [...prevStudents, data]);
          setNewStudent({
            name: '',
            rollNo: '',
            email: '',
            mobileNo: '',
          });
        } catch (error) {
          console.error('Error adding student:', error);
        }
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  const handleDeleteStudent = async (index) => {
    try {
      const deletedStudent = students[index];

      await axios.delete(`${BASE_URL}/students/${deletedStudent._id}`);
      const updatedStudents = students.filter((student) => student._id !== deletedStudent._id);

      setStudents(updatedStudents);
    } catch (error) {
      console.error('Error deleting student:', error);
    }
  };

  const handleEditStudent = (index) => {
    setEditingIndex(index);
    const selectedStudent = students[index];
    setNewStudent({
      name: selectedStudent.name,
      rollNo: selectedStudent.rollNo,
      email: selectedStudent.email,
      mobileNo: selectedStudent.mobileNo,
    });
  };

  const handleUpdateStudent = async () => {
    if (newStudent.name && newStudent.rollNo && newStudent.email && newStudent.mobileNo) {
      if (isDuplicateRollNo(newStudent.rollNo)) {
        alert('Student with the same Roll No already exists.');
      } else {
        try {
          const updatedStudent = students[editingIndex];
          await axios.put(`${BASE_URL}/students/${updatedStudent._id}`, newStudent);
          const updatedStudents = [...students];
          updatedStudents[editingIndex] = newStudent;
          setStudents(updatedStudents);
          setNewStudent({
            name: '',
            rollNo: '',
            email: '',
            mobileNo: '',
          });
          setEditingIndex(-1);
        } catch (error) {
          console.error('Error updating student:', error);
        }
      }
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <h2>Student Management</h2>
        <div className="form">
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={newStudent.name}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="rollNo"
            placeholder="Roll No"
            value={newStudent.rollNo}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={newStudent.email}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="mobileNo"
            placeholder="Mobile No"
            value={newStudent.mobileNo}
            onChange={handleInputChange}
          />
          <button onClick={handleAddStudent}>Add Student</button>
        </div>
        <table className="students-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Roll No</th>
              <th>Email</th>
              <th>Mobile No</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student, index) => (
              <tr key={student._id}>
                <td>{student.name}</td>
                <td>{student.rollNo}</td>
                <td>{student.email}</td>
                <td>{student.mobileNo}</td>
                <td>
                  <button onClick={() => handleEditStudent(index)}>Edit</button>
                  <button onClick={() => handleDeleteStudent(index)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {editingIndex !== -1 && (
          <div className="form">
            <h2>Edit Student</h2>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={newStudent.name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="rollNo"
              placeholder="Roll No"
              value={newStudent.rollNo}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="email"
              placeholder="Email"
              value={newStudent.email}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="mobileNo"
              placeholder="Mobile No"
              value={newStudent.mobileNo}
              onChange={handleInputChange}
            />
            <button onClick={handleUpdateStudent}>Update</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Student;
