import React, { useState } from 'react';
import './Student.css';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    id: '',
    email: '',
    phone: '',
  });

  const handleAddStudent = () => {
    if (newStudent.name && newStudent.id && newStudent.email && newStudent.phone) {
      setStudents([...students, newStudent]);
      setNewStudent({
        name: '',
        id: '',
        email: '',
        phone: '',
      });
    }
  };

  const handleDeleteStudent = (index) => {
    const updatedStudents = students.filter((_, i) => i !== index);
    setStudents(updatedStudents);
  };

  const handleUpdateStudent = (index, updatedStudent) => {
    const updatedStudents = [...students];
    updatedStudents[index] = updatedStudent;
    setStudents(updatedStudents);
  };

  return (
    <div className="student-page">
      <h2>Student Page</h2>

      <div className="student-form">
        <input
          type="text"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          placeholder="Enter student name"
        />
        <input
          type="text"
          value={newStudent.id}
          onChange={(e) => setNewStudent({ ...newStudent, id: e.target.value })}
          placeholder="Enter student ID"
        />
        <input
          type="email"
          value={newStudent.email}
          onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
          placeholder="Enter student email"
        />
        <input
          type="tel"
          value={newStudent.phone}
          onChange={(e) => setNewStudent({ ...newStudent, phone: e.target.value })}
          placeholder="Enter student phone number"
        />
        <button onClick={handleAddStudent}>Add Student</button>
      </div>

      <ul className="student-list">
        {students.map((student, index) => (
          <li key={index} className="student-item">
            <span>Name: {student.name}</span>
            <span>ID: {student.id}</span>
            <span>Email: {student.email}</span>
            <span>Phone: {student.phone}</span>
            <div className="student-actions">
              <button
                onClick={() =>
                  handleUpdateStudent(index, {
                    ...student,
                    name: `${student.name} Updated`,
                  })
                }
              >
                Update
              </button>
              <button onClick={() => handleDeleteStudent(index)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Student;
