import React, { useState } from 'react';
import './Student.css';

const Student = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    idNumber: '',
    email: '',
    phoneNumber: '',
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleAddStudent = () => {
    if (newStudent.name && newStudent.idNumber && newStudent.email && newStudent.phoneNumber) {
      setStudents([...students, newStudent]);
      setNewStudent({
        name: '',
        idNumber: '',
        email: '',
        phoneNumber: '',
      });
      setErrorMessage('');
    } else {
      setErrorMessage('Please fill in all sections.');
    }
  };

  const handleDeleteStudent = (index) => {
    const updatedStudents = students.filter((_, i) => i !== index);
    setStudents(updatedStudents);
  };

  const handleUpdateStudent = (index, field, value) => {
    const updatedStudents = [...students];
    updatedStudents[index][field] = value;
    setStudents(updatedStudents);
  };

  return (
    <div className="student-page">
      <h2>Student Page</h2>

      <div className="student-form">
        <input
          type="text"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          placeholder="Enter student name"
          required
        />
        <input
          type="text"
          value={newStudent.idNumber}
          onChange={(e) => setNewStudent({ ...newStudent, idNumber: e.target.value })}
          placeholder="Enter student ID number"
          required
        />
        <input
          type="email"
          value={newStudent.email}
          onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
          placeholder="Enter student email"
          required
        />
        <input
          type="tel"
          pattern="[0-9]{10}"
          value={newStudent.phoneNumber}
          onChange={(e) => setNewStudent({ ...newStudent, phoneNumber: e.target.value })}
          placeholder="Enter student phone number"
          required
        />
        <button onClick={handleAddStudent}>Add Student</button>
      </div>

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <ul className="student-list">
        {students.map((student, index) => (
          <li key={index} className="student-item">
            <span>{student.name}</span>
            <span>{student.idNumber}</span>
            <span>{student.email}</span>
            <span>{student.phoneNumber}</span>
            <div className="student-actions">
              <button onClick={() => handleUpdateStudent(index, 'name', `${student.name} Updated`)}>
                Update
              </button>
              <button onClick={() => handleDeleteStudent(index)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Student;
